<?php

namespace App\Http\ApiRequests\Tariffs;

use App\Http\ApiRequests\ApiRequest;
use Illuminate\Http\UploadedFile;
use Illuminate\Support\Facades\Storage;


/**
 * @OA\Post(
 *  path="/api/v3/tariff",
 *  operationId="tariff_store",
 *  summary="Store or Update tariff",
 *  security={{"sanctum": {} }},
 *  tags={"Tariffs"},
 *     @OA\RequestBody(
 *      @OA\JsonContent(
 *           @OA\Property(property="community_id", type="integer", example="1"),
 *           @OA\Property(property="test_period_is_active", type="boolean", example="true"),
 *           @OA\Property(property="title", type="string", example="My best tariff"),
 *           @OA\Property(property="tariff_is_payable", type="boolean", example="true"),
 *           @OA\Property(property="main_description", type="string", example="Pay me pls"),
 *           @OA\Property(property="main_image",  description="base64 image", type="integer", example="data:image/png;base64,"),
 *           @OA\Property(property="thanks_message_is_active", type="boolean", example="true"),
 *           @OA\Property(property="thanks_message", type="string", example="Spasibo for pay"),
 *           @OA\Property(property="thanks_image",  description="base64 image", type="boolean", example="data:image/png;base64,"),
 *           @OA\Property(property="price", type="integer", example="200"),
 *           required={"community_id", "title"},
 *         ),
 *
 *      ),
 *      @OA\Response(response=200, description="OK")
 * )
 */
class ApiTariffStoreRequest extends ApiRequest
{

    public function rules(): array
    {
        return [
            'community_id' => 'required|integer|exists:communities,id',
            'test_period_is_active' => 'boolean',
            'title' => 'required|string|max:150',
            'tariff_is_payable' => 'boolean',
            'main_description' => 'string|max:4096',
            'thanks_message_is_active' => 'boolean',
            'thanks_message' => 'string|max:940',
            'price' => 'integer',
        ];
    }

    public function prepareForValidation(): void
    {

        if ($this->request->get('main_image')) {
            $this->processBase64Image($this->request->get('main_image'), 'main_image');
        }
        if ($this->request->get('thanks_image')) {
            $this->processBase64Image($this->request->get('thanks_image'), 'thanks_image');
        }

    }

    public function processBase64Image($base64Image, $key)
    {
        $base64String = $base64Image;
        $base64Data = substr($base64String, strpos($base64String, ',') + 1);
        $file = base64_decode($base64Data, true);
        if ($file) {
            preg_match('/^data:image\/(\w+);base64,/', $base64String, $matches);
            $fileExtension = $matches[1];
            $filename = uniqid() . '-' . time();
            $filePath = 'tariff_images/' . $filename . '.' . $fileExtension;
            Storage::disk('public')->put($filePath, $file);

            $this->merge([
                $key => 'storage/' . $filePath
            ]);
        }
    }
}